import pytch


class Sky (pytch.Stage):
    Backdrops = ["clouds.jpg"]


class Bird(pytch.Sprite):
    Costumes = ["bird.png"]

    @pytch.when_green_flag_clicked
    def start(self):
        self.set_size(0.3)

    @pytch.when_key_pressed("ArrowRight")
    def move_right(self):
        self.change_x(3)

    @pytch.when_key_pressed("ArrowDown")
    def move_down(self):
        self.change_y(-3)
