import pytch
import random


class Bowl(pytch.Sprite):
    Costumes = ["bowl.png"]

    @pytch.when_green_flag_clicked
    def move_with_keys(self):
        self.go_to_xy(0, -145)
        while True:
            if pytch.key_pressed("ArrowLeft"):
                if self.x_position > -190:
                    self.change_x(-2)
            if pytch.key_pressed("ArrowRight"):
                if self.x_position < 190:
                    self.change_x(2)


class Fruit(pytch.Sprite):
    Costumes = ["apple.png", "orange.png"]
    score = 0

    @pytch.when_green_flag_clicked
    def move_down_stage(self):
        while True:
            self.switch_costume(1)
            self.go_to_xy(random.randint(-190,190), 200)
            self.show()
            while self.y_position > -140:
                self.change_y(-3)
                self.say(self.score)
                if self.touching(Bowl):
                    self.score = self.score + 5
                    self.hide()
